/**
 * Get the appropriate SVG icon filename for a given file extension
 * @param filename - The filename including extension
 * @param isDirectory - Whether this is a directory
 * @param isOpen - Whether the directory is open (for folders)
 * @returns The SVG filename to use for the icon
 */
export function getFileIcon(
  filename: string, 
  isDirectory: boolean = false, 
  isOpen: boolean = false
): string {
  if (isDirectory) {
    if (filename === '.' || filename === 'root' || filename.includes('project')) {
      return isOpen ? FALLBACK_ICONS.rootDirectoryOpen : FALLBACK_ICONS.rootDirectory;
    }
    return isOpen ? FALLBACK_ICONS.directoryOpen : FALLBACK_ICONS.directory;
  }

  // Handle full filenames first (like package.json, tsconfig.json, etc.)
  if (FILE_ICON_MAP[filename.toLowerCase()]) {
    return FILE_ICON_MAP[filename.toLowerCase()];
  }

  // Extract extension
  const lastDotIndex = filename.lastIndexOf('.');
  if (lastDotIndex === -1) {
    // No extension - check for special files
    const lowerFilename = filename.toLowerCase();
    
    // Common extensionless files
    const specialFiles: { [key: string]: string } = {
      'dockerfile': 'file_type_docker.svg',
      'makefile': 'file_type_makefile.svg',
      'rakefile': 'file_type_rake.svg',
      'gemfile': 'file_type_ruby.svg',
      'vagrantfile': 'file_type_vagrant.svg',
      'jenkinsfile': 'file_type_jenkins.svg',
      'procfile': 'file_type_procfile.svg',
      'cmakelists.txt': 'file_type_cmake.svg',
      'readme': 'file_type_markdown.svg',
      'changelog': 'file_type_markdown.svg',
      'license': 'file_type_license.svg',
      'authors': 'file_type_text.svg',
      'contributors': 'file_type_text.svg',
    };

    if (specialFiles[lowerFilename]) {
      return specialFiles[lowerFilename];
    }

    return FALLBACK_ICONS.default;
  }

  const extension = filename.substring(lastDotIndex + 1).toLowerCase();
  
  // Check if we have a specific icon for this extension
  if (FILE_ICON_MAP[extension]) {
    return FILE_ICON_MAP[extension];
  }

  // Fallback based on extension type
  const binaryExtensions = [
    'exe', 'dll', 'so', 'dylib', 'bin', 'app', 'deb', 'rpm',
    'msi', 'dmg', 'pkg', 'appimage'
  ];
  
  const textExtensions = [
    'txt', 'log', 'readme', 'changelog', 'authors', 'contributors',
    'todo', 'fixme', 'note', 'memo'
  ];

  const configExtensions = [
    'conf', 'config', 'cfg', 'ini', 'properties', 'settings'
  ];

  if (binaryExtensions.includes(extension)) {
    return FALLBACK_ICONS.binary;
  }
  
  if (textExtensions.includes(extension)) {
    return FALLBACK_ICONS.text;
  }
  
  if (configExtensions.includes(extension)) {
    return FALLBACK_ICONS.config;
  }

  // Ultimate fallback
  return FALLBACK_ICONS.default;
}

/**
 * Get the full path to the icon file
 * @param iconFilename - The SVG filename returned by getFileIcon
 * @returns Full path to the icon file
 */
export function getIconPath(iconFilename: string): string {
  return `/src/assets/${iconFilename}`;
}

/**
 * Check if an icon exists in our mapping
 * @param extension - File extension to check
 * @returns Whether we have a specific icon for this extension
 */
export function hasSpecificIcon(extension: string): boolean {
  return !!FILE_ICON_MAP[extension.toLowerCase()];
} 